import React, { createContext, useEffect, useState } from 'react';
// import all_product from '../Components/Assets/all_product';

export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let index=0; index< 300+1; index++) {
        cart[index]=0;
    }
    return cart;
}

const ShopContextProvider = (props) => {
    const [ all_product, setAllProduct ] = useState([]);
    const [ cartItems, setCartItems ] = useState(getDefaultCart());
    const [ menuState, setMenu ] = useState("shop");

    useEffect(() => {
        fetch('http://localhost:4000/all_product')
        .then((res) => res.json())
        .then((data) => setAllProduct(data))
    }, [])

    const addToCart = (itemId) => {
        setCartItems((prev) => ({...prev,[itemId]:prev[itemId]+1}));
        if(localStorage.getItem('auth-token')) {
            fetch('http://localhost:4000/add_cart', {
                method: 'POST',
                headers: {
                    Accept: app
                }
            })
        }
    }

    const removeFromCart = (itemId) => {
        setCartItems((prev) => ({...prev,[itemId]:prev[itemId]-1}));
    }

    const updateMenu = (category) => {
        setMenu(category);
    }

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            if(cartItems[item] > 0) {
                let itemInfo = all_product.find((product) => product.id===Number(item))
                totalAmount += itemInfo.new_price * cartItems[item];
            }
        }
        return totalAmount;
    }

    const getTotalCartItem = () => {
        let totalItem = 0;
        for (const item in cartItems) {
            if(cartItems[item]>0) {
                totalItem+=cartItems[item];
            }
        }
        return totalItem;
    }

    const contextValue = {all_product, cartItems, menuState, addToCart, removeFromCart, updateMenu, getTotalCartAmount, getTotalCartItem};

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    )
}

export default ShopContextProvider;
